mapper.select:
select 

from bardata
where sid  = #{id} and dateTime >= #{time}
order by dateTime ASC limit #{size}

mapper.insert:
SELECT LAST_INSERT_ID()
insert into bardata (id, dateTime, open, height, low, close, ystClose, volume, matchItems, sId) values
#{item.id,jdbcType=INTEGER}, #{item.datetime,jdbcType=DATE}, #{item.open,jdbcType=VARCHAR}, #{item.height,jdbcType=VARCHAR}, #{item.low,jdbcType=VARCHAR}, #{item.close,jdbcType=VARCHAR}, #{item.ystclose,jdbcType=VARCHAR}, #{item.volume,jdbcType=VARCHAR}, #{item.matchitems,jdbcType=VARCHAR}, #{item.sid,jdbcType=INTEGER}
